import React, { useState } from 'react';
import { Nav, Navbar, Tab, Row, Col, Container } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom'; // Import Routes
import UserForm from './UserForm';
import UserList from './UserList';

// Example new page component
const About = () => (
    <div>
        <h2>About Us</h2>
        <p>This is the About page of the User Management App.</p>
    </div>
);

function App() {
    const [users, setUsers] = useState([]);

    return (
        <Router> {/* Wrap the entire app in Router */}
            <div>
                {/* Navigation Bar */}
                <Navbar bg="primary" variant="dark">
                    <Navbar.Brand href="#home">User Management</Navbar.Brand>
                    <Nav className="ml-auto">
                        <Nav.Link as={Link} to="/">Home</Nav.Link> {/* Link to Home page */}
                        <Nav.Link as={Link} to="/about">About</Nav.Link> {/* Link to About page */}
                    </Nav>
                </Navbar>

                {/* Main Content with Tabs */}
                <Container className="mt-3">
                    <Routes> {/* Use Routes instead of Switch */}
                        {/* Route for Home Page */}
                        <Route path="/" element={
                            <Tab.Container id="left-tabs-example" defaultActiveKey="userForm">
                                <Row>
                                    <Col sm={3}>
                                        <Nav variant="pills" className="flex-column">
                                            <Nav.Item>
                                                <Nav.Link eventKey="userForm">User Form</Nav.Link>
                                            </Nav.Item>
                                            <Nav.Item>
                                                <Nav.Link eventKey="userList">User List</Nav.Link>
                                            </Nav.Item>
                                        </Nav>
                                    </Col>
                                    <Col sm={9}>
                                        <Tab.Content>
                                            <Tab.Pane eventKey="userForm">
                                                <UserForm setUsers={setUsers} />
                                            </Tab.Pane>
                                            <Tab.Pane eventKey="userList">
                                                <UserList users={users} setUsers={setUsers} />
                                            </Tab.Pane>
                                        </Tab.Content>
                                    </Col>
                                </Row>
                            </Tab.Container>
                        } />

                        {/* Route for About Page */}
                        <Route path="/about" element={<About />} />
                    </Routes>
                </Container>
            </div>
        </Router>
    );
}

export default App;
