import React, { useState } from 'react';
import { Nav, Navbar, Tab, Row, Col, Container } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import UserForm from './UserForm';
import UserList from './UserList';

function App() {
    const [users, setUsers] = useState([]); // State to hold users

    return (
        <div>
            { /* Navigation Bar */}
            <Navbar bg="dark" variant="dark">
                <Navbar.Brand href="#home">User Management</Navbar.Brand>
                <Nav className="ml-auto">
                    <Nav.Link href="#home">Home</Nav.Link>
                </Nav>
            </Navbar>

            { /* Main Content with Tabs */ }
            <Container className="mt-3">
                <Tab.Container id="left-tabs-example" defaultActiveKey="userForm">
                    <Row>
                        <Col sm={3}>
                            <Nav variant="pills" className="flex-column">
                                <Nav.Item>
                                    <Nav.Link eventKey="userForm">User Form</Nav.Link>
                                </Nav.Item>
                                <Nav.Item>
                                    <Nav.Link eventKey="userList">User List</Nav.Link>
                                </Nav.Item>
                            </Nav>
                        </Col>
                        <Col sm={9}>
                            <Tab.Content>
                                { /* User Form Tab */ }
                                <Tab.Pane eventKey="userForm">
                                    <UserForm setUsers={setUsers} />
                                </Tab.Pane>
                                { /* User List Tab */ }
                                <Tab.Pane eventKey="userList">
                                    <UserList users={users} setUsers={setUsers} />
                                </Tab.Pane>
                            </Tab.Content>
                        </Col>
                    </Row>
                </Tab.Container>
            </Container>
        </div>
    );
}

export default App;
